mapper.select:
select COUNT(id)
from tb_video

  id = #{id}
  AND title like CONCAT('%',#{title},'%')
  AND tag like CONCAT('%',#{tag},'%')
  AND description like CONCAT('%',#{description},'%')
  AND status = #{status}
  AND forbidden_reason like CONCAT('%',#{forbiddenReason},'%')
  AND top = #{top}
  AND like_time = #{likeTime}
  AND play_time = #{playTime}
  AND discuss_time = #{discussTime}
  AND enshrine_time = #{enshrineTime}
  AND share_time = #{shareTime}
  AND top_time = #{topTime}
  AND create_time = #{createTime}
  AND user_id = #{userId}
  AND duration = #{duration}

    AND status IN

      #{statusitem}



    AND  create_time>=#{startTime} 


    AND  create_time<=#{endTime} 


ORDER BY ${sortExp}

select id, title, tag,description, status, top, like_time, play_time, discuss_time,enshrine_time,share_time, duration, attachment_id, transform_attachment_id, top_time,create_time, user_id
from tb_video

  id = #{id}
  AND title like CONCAT('%',#{title},'%')
  AND tag like CONCAT('%',#{tag},'%')
  AND description like CONCAT('%',#{description},'%')
  AND status = #{status}
  AND forbidden_reason like CONCAT('%',#{forbiddenReason},'%')
  AND top = #{top}
  AND like_time = #{likeTime}
  AND play_time = #{playTime}
  AND discuss_time = #{discussTime}
  AND enshrine_time = #{enshrineTime}
  AND share_time = #{shareTime}
  AND top_time = #{topTime}
  AND create_time = #{createTime}
  AND user_id = #{userId}
  AND duration = #{duration}

    AND status IN

      #{statusitem}



    AND  create_time>=#{startTime} 


    AND  create_time<=#{endTime} 


ORDER BY ${sortExp}
limit #{startRow},#{pageSize}

select id, title, tag,description, status, top, like_time, play_time, discuss_time,enshrine_time,share_time,duration,attachment_id, transform_attachment_id, top_time, create_time, user_id
from tb_video
where id = #{id,jdbcType=BIGINT}
